DROP TABLE IF EXISTS "users", "parking_space", "user_parking_space", "microcontroller", "slot", "sensor", "rfid", "entry_log", "door", "lcd";

CREATE TABLE "users" (
                         "user_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                         "username" varchar UNIQUE NOT NULL,
                         "email" varchar UNIQUE NOT NULL,
                         "password_hash" varchar NOT NULL,
                         "role" varchar NOT NULL,
                         "enabled" boolean DEFAULT true,
                         "created_at" timestamp
);

CREATE TABLE "parking_space" (
                                 "ps_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                                 "name" varchar,
                                 "location" varchar,
                                 "owner" varchar NOT NULL
);

CREATE TABLE "user_parking_space" (
                                      "user_id" int NOT NULL,
                                      "ps_id" int NOT NULL,
                                      PRIMARY KEY ("user_id", "ps_id")
);

CREATE TABLE "microcontroller" (
                                   "mc_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                                   "mc_code" varchar UNIQUE NOT NULL,
                                   "name" varchar,
                                   "online" boolean DEFAULT false,
                                   "uptime_sec" bigint,
                                   "last_seen" timestamp,
                                   "ps_id" int NOT NULL
);

CREATE TABLE "door" (
                         "door_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                         "name" varchar,
                         "is_opened" boolean DEFAULT FALSE,
                         "mc_id" int NOT NULL
);

CREATE TABLE "lcd" (
                        "lcd_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                        "name" varchar,
                        "display" text,
                        "mc_id" int NOT NULL
);

CREATE TABLE "sensor" (
                          "sensor_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                          "name" varchar,
                          "type" varchar,
                          "slot_id" int NOT NULL,
                          "mc_id" int NOT NULL
);

CREATE TABLE "slot" (
                        "slot_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                        "ps_id" int NOT NULL,
                        "is_occupied" boolean DEFAULT false
);

CREATE TABLE "rfid" (
                        "rfid_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                        "rfid_code" varchar UNIQUE NOT NULL,
                        "ps_id" int NOT NULL,
                        "currently_used" boolean DEFAULT false
);

CREATE TABLE "entry_log" (
                             "log_id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
                             "rfid_id" int NOT NULL,
                             "license_plate" varchar,
                             "in_time" timestamp,
                             "out_time" timestamp
);

COMMENT ON COLUMN "users"."role" IS 'ROLE_USER | ROLE_ADMIN';
ALTER TABLE "user_parking_space" ADD FOREIGN KEY ("user_id") REFERENCES "users" ("user_id");
ALTER TABLE "user_parking_space" ADD FOREIGN KEY ("ps_id") REFERENCES "parking_space" ("ps_id");
ALTER TABLE "microcontroller" ADD FOREIGN KEY ("ps_id") REFERENCES "parking_space" ("ps_id");
ALTER TABLE "door" ADD FOREIGN KEY ("mc_id") REFERENCES "microcontroller" ("mc_id");
ALTER TABLE "lcd" ADD FOREIGN KEY ("mc_id") REFERENCES "microcontroller" ("mc_id");
ALTER TABLE "sensor" ADD FOREIGN KEY ("slot_id") REFERENCES "slot" ("slot_id");
ALTER TABLE "sensor" ADD FOREIGN KEY ("mc_id") REFERENCES "microcontroller" ("mc_id");
ALTER TABLE "slot" ADD FOREIGN KEY ("ps_id") REFERENCES "parking_space" ("ps_id");
ALTER TABLE "rfid" ADD FOREIGN KEY ("ps_id") REFERENCES "parking_space" ("ps_id");
ALTER TABLE "entry_log" ADD FOREIGN KEY ("rfid_id") REFERENCES "rfid" ("rfid_id");
